package org.rr.commons.swing.icon;import java.awt.Component;import java.awt.Dimension;import java.awt.Graphics;import javax.swing.Icon;/** * This {@link Icon} draws nothing. It can be usefull * for place holding etc. */public class EmptyIcon implements Icon {        protected int width = 14;        protected int height = 14;        protected boolean mouseOver;        public EmptyIcon(int width, int height) {    	this.width = width;    	this.height = height;    }        public EmptyIcon() {    }        public void setIconSize(Dimension size) {    	this.width = size.width;    	this.height = size.height;    }        public int getIconWidth() {        return width;    }        public int getIconHeight() {        return height;    }        public void paintIcon(Component c, Graphics g, int x, int y) {    }}